package looping;
import java.util.*;

public class Whileexecutor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int i=1;
		//printing n to 1
		while(n>=i)
		{
			System.out.print(n+" ");
			n--;
		}

	}

}
